import React from 'react'
import { useState, useEffect } from 'react'
import axios from 'axios'
import { useQuery } from 'react-query';
import { IoMdSearch } from "react-icons/io";
import { SiToyota } from "react-icons/si";



function Renderinfo() {

    const [Query, setQuery] = useState([]);
    const [resultQuery, setresultQuery] = useState([]);


    useEffect(() => {
        fetch('http://localhost:8000/allproduct')
            .then(res => res.json())
            .then(data => {
                setresultQuery(data);
            }).catch((e) => { console.log(e) });
    }, []);

    const handleSearch = (evt) => {

        fetch('http://localhost:8000/product/search/' + evt)
            .then(res => res.json())
            .then(data => {

                setQuery(data);
            }).catch((e) => { console.log(e) });
    }





    return (
        <div className="mt-[50px] md:mt-2">

            <div className="flex justify-center ">
                <div className="text-5xl text-white font-serif"> <span className="text-[#ea455d]">Your best cars,</span><br /> with stress free</div>
            </div>
            <div className="flex justify-center mt-[30px] md:w-[50em] md:ml-[15em] ">
                <IoMdSearch className="text-2xl text-white" />
                <button className="text-white">ESC</button>
                <input
                    type="input"
                    className=" h-[45px] font-serif md:w-[40em]  w-[18em] shadow-lg border-2" placeholder="Search for a Car"
                    onChange={(evt) => handleSearch(evt.target.value)}
                    style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                    }}
                />

            </div>

            <div className=" searz md:ml-[22em] h-[19.9em] overflow-y-scroll md:w-[40em] ml-[6em] w-[20em] bg-white">
                {resultQuery.map((item, index) => (
                    <div className="border-[1px] mx-4 my-4 flex h-[100px] shadow-lg" style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                    }}>

                        <p>{item.name}</p>
                        <p>{item.yrs_production}</p>
                        <SiToyota />
                        <span className="absolute mt-40 ml-40">₦{item.amount}</span>
                        <img src="/images/cars/1.png" alt="img" className="w-40 h-20 ml-[22em] mt-[-2.5em] absolute " />

                    </div>
                ))}

            </div>

        </div >
    )
}

export default Renderinfo
