import React from 'react'
import { useKeenSlider } from "keen-slider/react"
import "keen-slider/keen-slider.min.css"

function RenderSearch() {

    const [pause, setPause] = React.useState(false)
    const timer = React.useRef()
    const [sliderRef, slider] = useKeenSlider({
        loop: true,
        duration: 1000,
        spacing: 10,
        slidesPerView: 1,
        centered: true,
        loop: true,
        mode: "snap",
        breakpoints: {
            "(min-width: 768px)": {
                slidesPerView: 2,
                mode: "free-snap",
            },
            "(min-width: 1200px)": {
                slidesPerView: 3,
                mode: "free-snap",
            },
        },
        dragStart: () => {
            setPause(true)
        },
        dragEnd: () => {
            setPause(false)
        },
    })

    React.useEffect(() => {
        sliderRef.current.addEventListener("mouseover", () => {
            setPause(true)
        })
        sliderRef.current.addEventListener("mouseout", () => {
            setPause(false)
        })
    }, [sliderRef])

    React.useEffect(() => {
        timer.current = setInterval(() => {
            if (!pause && slider) {
                slider.next()
            }
        }, 2000)
        return () => {
            clearInterval(timer.current)
        }
    }, [pause, slider])

    return (

        // <div ref={sliderRef} className="keen-slider">
        //     {/* // <div  className="keen-slider"> */}
        //     <div className="keen-slider__slide number-slide1 pos">

        //         <img src="/images/1.png" className="slidimg" />

        //     </div>
