        //         <img src="/images/1.png" className="slidimg" />

        //     </div>
        //     <div className="keen-slider__slide number-slide4 pos">

        //         <img src="/images/1.png" className="slidimg" />

        //     </div>
        //     <div className="keen-slider__slide number-slide5 pos">

        //         <img src="/images/1.png" className="slidimg" />

        //     </div>
        //     <div className="keen-slider__slide number-slide6 pos">


        //         <img src="/images/1.png" className="slidimg" />

        //     </div>
        // </div>

        <div className="mt-10 mx-6 flex space-x-4 keen-slider" ref={sliderRef} >
            <div className="border-2 h-[16em] w-[20em] keen-slider__slide number-slide1"
                style={{
                    borderTopRightRadius: '10px',
                    borderBottomRightRadius: '10px',
                    borderTopLeftRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',
                }}
            >
                <div className="flex justify-center" style={{ zIndex: "99" }}>
                    <img src="/images/sliders/3.jpg" className="h-[12em] w-[20em]" style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                        zIndex: '99'
                    }} />
                </div>
                <div className="bg-black  mt-[-20px] z-0 h-20" style={{
                    borderBottomRightRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',

                }} >
                    <div className="h-6">

                    </div>

                    <div className=" flex justify-center">
                        <h1 className=" font-serif text-2xl text-[#ea455d] ">4-matic gl350 </h1> <br />
                    </div>
                    <div className="flex justify-center">
                        <p className=" text-2lg text-white">₦50m</p>
                    </div>
                </div>
            </div>


            {/* 2 */}
            <div className="border-2 h-[16em] w-[20em] keen-slider__slide number-slide1"
                style={{
                    borderTopRightRadius: '10px',
                    borderBottomRightRadius: '10px',
                    borderTopLeftRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',
                }}
            >
                <div className="flex justify-center" style={{ zIndex: "99" }}>
                    <img src="/images/sliders/3.jpg" className="h-[12em] w-[20em]" style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                        zIndex: '99'
                    }} />
                </div>
                <div className="bg-black  mt-[-20px] z-0 h-20" style={{
                    borderBottomRightRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',

                }} >
                    <div className="h-6">

                    </div>

                    <div className=" flex justify-center">
                        <h1 className=" font-serif text-2xl text-[#ea455d] ">4-matic gl350 </h1> <br />
                    </div>
                    <div className="flex justify-center">
                        <p className=" text-2lg text-white">₦50m</p>
                    </div>
                </div>
            </div>

            {/* 3 */}

            <div className="border-2 h-[16em] w-[20em] keen-slider__slide number-slide1"
                style={{
                    borderTopRightRadius: '10px',
                    borderBottomRightRadius: '10px',
                    borderTopLeftRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',
                }}
            >
                <div className="flex justify-center" style={{ zIndex: "99" }}>
                    <img src="/images/sliders/3.jpg" className="h-[12em] w-[20em]" style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                        zIndex: '99'
                    }} />
                </div>
                <div className="bg-black  mt-[-20px] z-0 h-20" style={{
                    borderBottomRightRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',

                }} >
                    <div className="h-6">

                    </div>

                    <div className=" flex justify-center">
                        <h1 className=" font-serif text-2xl text-[#ea455d] ">4-matic gl350 </h1> <br />
                    </div>
                    <div className="flex justify-center">
                        <p className=" text-2lg text-white">₦50m</p>
                    </div>
                </div>
            </div>

            {/* 4 */}

            <div className="border-2 h-[16em] w-[20em] keen-slider__slide number-slide1"
                style={{
                    borderTopRightRadius: '10px',
                    borderBottomRightRadius: '10px',
                    borderTopLeftRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',
                }}
            >
                <div className="flex justify-center" style={{ zIndex: "99" }}>
                    <img src="/images/sliders/3.jpg" className="h-[12em] w-[20em]" style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                        zIndex: '99'
                    }} />
                </div>
                <div className="bg-black  mt-[-20px] z-0 h-20" style={{
                    borderBottomRightRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',

                }} >
                    <div className="h-6">

                    </div>

                    <div className=" flex justify-center">
                        <h1 className=" font-serif text-2xl text-[#ea455d] ">4-matic gl350 </h1> <br />
                    </div>
                    <div className="flex justify-center">
                        <p className=" text-2lg text-white">₦50m</p>
                    </div>
                </div>
            </div>

        </div>
    )
}

export default RenderSearch
