import { useState, useEffect } from 'react'
import { useKeenSlider } from "keen-slider/react"
import Renderinfo from './Renderinfo'
import { Router, useRouter } from 'next/router'



// export const getServerSideProps = async (ctx) => {
//     const res = axios.get('http://localhost:8000/products');
//     const data = await res.json();
//     console.log(data)
//     return {
//         props: { product: data }
//     }
// }




function RenderSearch({ product }) {
    const [isLoading, setisLoading] = useState(true)
    const [dashData, setDashData] = useState(null)


    useEffect(() => {
        async function fetchdata() {
            const resp = await fetch('http://localhost:8000/products')
            const data = await resp.json()

            setDashData(data)
            setisLoading(fals)
        }

        fetchdata()
    }, [])

    const router = useRouter()

    const [pause, setPause] = React.useState(false)
    const timer = React.useRef()
    const [sliderRef, slider] = useKeenSlider({
        loop: true,
        duration: 1000,
        spacing: 20,
        slidesPerView: 1,
        centered: true,
        loop: true,
        mode: "snap",
        breakpoints: {
            "(min-width: 768px)": {
                slidesPerView: 2,
                mode: "free-snap",
            },
            "(min-width: 1200px)": {
                slidesPerView: 3,
                mode: "free-snap",
            },
        },
        dragStart: () => {
            setPause(true)
        },
        dragEnd: () => {
            setPause(false)
        },
    })

    React.useEffect(() => {
        sliderRef.current.addEventListener("mouseover", () => {
            setPause(true)
        })
        sliderRef.current.addEventListener("mouseout", () => {
            setPause(false)
        })
    }, [sliderRef])

    React.useEffect(() => {
        timer.current = setInterval(() => {
            if (!pause && slider) {
                slider.next()

            }
        }, 2000)
        return () => {
            clearInterval(timer.current)
        }
    }, [pause, slider])


    return (

        <div className="mt-1    0 mx-6 md:flex md:space-x-4 keen-slider" ref={sliderRef} >
            {console.log(dashData)}
            <div className="border-2 h-[16em] w-[4em] keen-slider__slide sol"
                style={{
                    borderTopRightRadius: '10px',
                    borderBottomRightRadius: '10px',
                    borderTopLeftRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',
                }}
                onClick={() => {
                    router.push('/item_info')
                }}>
                <div className="flex justify-center" style={{ zIndex: "99" }}>
                    <img src="/images/sliders/3.jpg" className="h-[12em] w-[20em]" style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                        zIndex: '99'
                    }} />
                </div>
                <div className="bg-black  mt-[-20px] z-0 h-20" style={{
                    borderBottomRightRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',

                }} >
                    <div className="h-6">

                    </div>

                    <div className=" flex justify-center">
                        <h1 className=" font-serif text-2xl text-[#ea455d] ">4-matic gl350 </h1> <br />
                    </div>
                    <div className="flex justify-center">
                        <p className=" text-2lg text-white">₦50m</p>
                    </div>
                </div>
            </div>



            {/* 2 */}
            <div className="border-2 h-[16em] w-[10em] keen-slider__slide sol"
                style={{
                    borderTopRightRadius: '10px',
                    borderBottomRightRadius: '10px',
                    borderTopLeftRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',
                }}

                onClick={() => {
                    router.push('/item_info')
                }}
            >
                <div className="flex justify-center" style={{ zIndex: "99" }}>
                    <img src="/images/sliders/3.jpg" className="h-[12em] w-[20em]" style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                        zIndex: '99'
                    }} />
                </div>
                <div className="bg-black  mt-[-20px] z-0 h-20" style={{
                    borderBottomRightRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',

                }} >
                    <div className="h-6">

                    </div>

                    <div className=" flex justify-center">
                        <h1 className=" font-serif text-2xl text-[#ea455d] ">4-matic gl350 </h1> <br />
                    </div>
                    <div className="flex justify-center">
                        <p className=" text-2lg text-white">₦50m</p>
                    </div>
                </div>
            </div>

            {/* 3 */}

            <div className="border-2 h-[16em] w-[10em] keen-slider__slide sol"
                style={{
                    borderTopRightRadius: '10px',
                    borderBottomRightRadius: '10px',
                    borderTopLeftRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',
                }}

                onClick={() => {
                    router.push('/item_info')
                }}
            >
                <div className="flex justify-center" style={{ zIndex: "99" }}>
                    <img src="/images/sliders/3.jpg" className="h-[12em] w-[20em]" style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                        zIndex: '99'
                    }} />
                </div>
                <div className="bg-black  mt-[-20px] z-0 h-20 sol" style={{
                    borderBottomRightRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',

                }} >
                    <div className="h-6">

                    </div>

                    <div className=" flex justify-center">
                        <h1 className=" font-serif text-2xl text-[#ea455d] ">4-matic gl350 </h1> <br />
                    </div>
                    <div className="flex justify-center">
                        <p className=" text-2lg text-white">₦50m</p>
                    </div>
                </div>
            </div>

            {/* 4 */}

            <div className="border-2 h-[16em] w-[10em] keen-slider__slide sol"
                style={{
                    borderTopRightRadius: '10px',
                    borderBottomRightRadius: '10px',
                    borderTopLeftRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',
                }}
                onClick={() => {
                    router.push('/item_info')
                }}
            >
                <div className="flex justify-center" style={{ zIndex: "99" }}>
                    <img src="/images/sliders/3.jpg" className="h-[12em] w-[20em]" style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                        zIndex: '99'
                    }} />
                </div>
                <div className="bg-black  mt-[-20px] z-0 h-20" style={{
                    borderBottomRightRadius: '10px',
                    borderBottomLeftRadius: '10px',
                    outline: 'none',

                }} >
                    <div className="h-6">

                    </div>

                    <div className=" flex justify-center">
                        <h1 className=" font-serif text-2xl text-[#ea455d] ">4-matic gl350 </h1> <br />
                    </div>
                    <div className="flex justify-center">
                        <p className=" text-2lg text-white">₦50m</p>
                    </div>
                </div>
            </div>




        </div>
    )
}

export default RenderSearch
